// Validate Stripe webhook signature and event structure
const crypto = require('crypto');

const validateStripeSignature = (payload, signature, secret) => {
  const expectedSignature = crypto
    .createHmac('sha256', secret)
    .update(payload, 'utf8')
    .digest('hex');
  return `sha256=${expectedSignature}` === signature;
};

const event = $input.all()[0].json;
const signature = $input.all()[0].headers['stripe-signature'];
const webhookSecret = $env.STRIPE_WEBHOOK_SECRET;

// Validate signature
if (!validateStripeSignature(JSON.stringify(event), signature, webhookSecret)) {
  throw new Error('Invalid Stripe signature');
}

// Extract event details
const eventData = {
  id: event.id,
  type: event.type,
  created: event.created,
  customer_id: event.data?.object?.customer,
  amount: event.data?.object?.amount_total || event.data?.object?.amount,
  currency: event.data?.object?.currency || 'usd',
  subscription_id: event.data?.object?.subscription,
  invoice_id: event.data?.object?.invoice,
  metadata: event.data?.object?.metadata || {},
  raw_event: event
};

// Financial tracking data
eventData.financial_impact = {
  revenue_event: ['payment_intent.succeeded', 'invoice.payment_succeeded', 'subscription_schedule.created'].includes(event.type),
  trial_event: ['customer.subscription.trial_will_end', 'subscription_schedule.created'].includes(event.type),
  conversion_event: event.type === 'customer.subscription.created' && event.data.object.trial_end,
  amount_usd: eventData.amount ? (eventData.amount / 100) : 0
};

return [eventData];

// Process trial conversion events
const eventData = $input.all()[0].json;

// Calculate conversion metrics
const conversionData = {
  customer_id: eventData.customer_id,
  subscription_id: eventData.subscription_id,
  conversion_timestamp: new Date().toISOString(),
  trial_end_date: eventData.raw_event.data.object.trial_end,
  plan_id: eventData.raw_event.data.object.items.data[0].price.id,
  monthly_value: eventData.amount_usd,
  conversion_source: eventData.metadata.source || 'direct',

  // Financial impact
  financial_impact: {
    event_type: 'trial_conversion',
    monthly_recurring_revenue: eventData.amount_usd,
    annual_value: eventData.amount_usd * 12,
    conversion_date: new Date().toISOString()
  },

  // CRM update data
  crm_update: {
    status: 'converted_customer',
    lifecycle_stage: 'customer',
    conversion_date: new Date().toISOString(),
    ltv_estimate: eventData.amount_usd * 24, // 2 year LTV estimate
    tier: eventData.amount_usd >= 299 ? 'enterprise' : eventData.amount_usd >= 99 ? 'pro' : 'basic'
  }
};

return [conversionData];

// Process successful payment events
const eventData = $input.all()[0].json;

const paymentData = {
  customer_id: eventData.customer_id,
  payment_id: eventData.id,
  amount_usd: eventData.amount_usd,
  payment_timestamp: new Date().toISOString(),
  payment_method: eventData.raw_event.data.object.payment_method_types?.[0] || 'card',

  // Revenue tracking
  revenue_impact: {
    event_type: 'payment_success',
    daily_revenue_contribution: eventData.amount_usd,
    payment_date: new Date().toISOString(),
    recurring: eventData.subscription_id ? true : false
  },

  // Dashboard metrics update
  dashboard_update: {
    daily_revenue: eventData.amount_usd,
    customer_count: 1,
    payment_success_rate: 'increment',
    mrr_change: eventData.subscription_id ? eventData.amount_usd : 0
  }
};

return [paymentData];

// Process payment failure events
const eventData = $input.all()[0].json;

const failureData = {
  customer_id: eventData.customer_id,
  payment_id: eventData.id,
  failure_reason: eventData.raw_event.data.object.last_payment_error?.code || 'unknown',
  failure_timestamp: new Date().toISOString(),

  // Risk assessment
  risk_data: {
    event_type: 'payment_failure',
    failure_code: eventData.raw_event.data.object.last_payment_error?.code,
    retry_required: true,
    customer_at_risk: true
  },

  // CRM follow-up
  crm_action: {
    trigger_dunning: true,
    update_payment_status: 'failed',
    schedule_retry: new Date(Date.now() + 24*60*60*1000).toISOString() // 24 hours
  }
};

return [failureData];

// Track progress toward $600/day Week 2 target
const eventData = $input.all()[0].json;
const today = new Date().toISOString().split('T')[0];

// Calculate daily revenue contribution
const dailyContribution = eventData.financial_impact?.monthly_recurring_revenue || 
                         eventData.revenue_impact?.daily_revenue_contribution || 0;

// Week 2 progress tracking
const progressData = {
  date: today,
  revenue_contribution: dailyContribution,
  week2_target: 600, // $600/day target
  event_type: eventData.financial_impact?.event_type || eventData.revenue_impact?.event_type,
  customer_id: eventData.customer_id,

  // Progress metrics
  progress_metrics: {
    daily_target: 600,
    contribution: dailyContribution,
    cumulative_tracking: true,
    conversion_quality: eventData.crm_update?.tier || 'standard'
  },

  // Success indicators
  milestone_progress: {
    stripe_integration_active: true,
    payment_processing_live: eventData.revenue_impact?.event_type === 'payment_success',
    trial_conversions_working: eventData.financial_impact?.event_type === 'trial_conversion',
    revenue_tracking_operational: true
  }
};

// Store in Week 2 tracking system
return [progressData];

// Send notifications for important events
const progressData = $input.all()[0].json;
const eventAmount = progressData.revenue_contribution;

const notifications = [];

// Revenue milestone notifications
if (eventAmount >= 100) {
  notifications.push({
    type: 'revenue_milestone',
    message: `ðŸŽ‰ Significant revenue event: $${eventAmount} from ${progressData.customer_id}`,
    urgency: 'high',
    channel: 'slack'
  });
}

// Daily target progress
if (progressData.milestone_progress.payment_processing_live) {
  notifications.push({
    type: 'system_operational',
    message: `âœ… Payment processing operational - Week 2 progress on track`,
    urgency: 'medium',
    channel: 'email'
  });
}

// Trial conversion success
if (progressData.milestone_progress.trial_conversions_working) {
  notifications.push({
    type: 'conversion_success',
    message: `ðŸŽ¯ Trial conversion successful - $${eventAmount}/month added to MRR`,
    urgency: 'high', 
    channel: 'slack'
  });
}

return notifications.map(notif => ({ json: notif }));

